const sendBtn=document.getElementById("btn-send");sendBtn.addEventListener("click",e=>{e.preventDefault();const t=document.getElementById("input_name"),n=document.getElementById("input_email"),a=document.getElementById("input_message"),s=document.getElementById("contact-msg"),o={name:t.value,email:n.value,message:a.value};$.ajax({type:"POST",url:"/contact",datatype:"application/json",data:o}).done(e=>{const o="0px 5px 20px 0px rgba(67, 76, 172, 0.3)";if(e.errors){const o=e.errors,c="0px 5px 20px 0px rgba(255, 0, 0, 0.7)";o.map(e=>{switch(s.classList.add("error"),e.param.toString()){case"name":t.style.boxShadow=c;break;case"email":n.style.boxShadow=c;break;case"message":a.style.boxShadow=c}})}else e.nodemailerError?(s.classList.add("error"),s.innerHTML=e.nodemailerError):(t.style.boxShadow=o,n.style.boxShadow=o,a.style.boxShadow=o,s.classList.add("success"),s.innerText=e.success)}).fail(e=>console.log(e))}),$(document).ready(function(){$.ajax({type:"GET",url:"/work",success:function(e){e.forEach(e=>{!function(e){$(".work-portfolio-container").append(`\n            <div class="item">\n                <div class="item-image" style="background-image: url(${e.image})">\n                </div>\n                <div class="item-content">\n                    <h1 class="item-content-header">${e.header}</h1>\n                    <p class="item-content-text">${e.text}.</p>\n                    <div class="item-content-buttons">\n                        <a href="${e.link}" target="_blank">\n                            <span class="btn-visit">Visit Site</span>\n                            <div class="rec"></div>\n                        </a>\n                    </div>\n                </div>\n          </div>`)}(e)})}})});const navContactBtn=document.getElementById("nav-contact-btn");navContactBtn.addEventListener("click",function(){document.querySelector("#contact").scrollIntoView({top:-200,left:0,behavior:"smooth"})});